            context.SaveChanges();

            var exhibits = new List<Exhibit>
            {
                new Exhibit() { Code = "cat_pope", Name = "Папство", Location = "Католицизм", Description = "<p>Папство &mdash; богословский и религиозно-политический институт католицизма, устанавливающий Папу Римского видимым главой всей Католической церкви. В Католической церкви Папа также является верховным правителем Святого Престола, вспомогательной суверенной территорией которого является Ватикан, где находится его постоянная резиденция.</p>" },
                new Exhibit() { Code = "cat_latcross", Name = "Латинский крест", Location = "Католицизм", Description = "<p>Латинский крест, также называемый длинный крест, крест Распятия, крест Запада, крест Жизни, крест Страдания &mdash; четырёхконечный крест, у которого вертикальная перекладина (лат. staticulum) длиннее горизонтальной (лат. patibulum), которая расположена выше середины вертикальной и делится ею пополам.</p>  <p>Наиболее распространённый христианский символ в мире. Он является преобладающим в католичестве, где этот вид креста считается реальной формой креста, на котором был распят Иисус Христос. Кроме того, три коротких конца могут представляться как три лица Троицы, а длинный конец &mdash; единичность Бога.</p>  <p>Крест с фигурой Иисуса называют Распятие и используют как декоративный элемент украшения стен. Часто католические церкви имеют форму латинского креста: основной неф представляет собой вертикальную перекладину креста, а поперечный неф (трансепт) &mdash; горизонтальную.</p>" },
                new Exhibit() { Code = "cat_petercross", Name = "Крест Св. Петра", Location = "Католицизм", Description = "<p>Крест Святого Петра (также известный как перевёрнутый крест) &mdash; обычный латинский крест (изображённый в соответствии с римско-католической традицией), перевёрнутый на 180 градусов. Крест святого Петра с 4 века является одним из символов святого Петра, который, по церковному преданию, был распят головой вниз в 67 году н.э. во время правления в Риме императора Нерона.</p>  <p>Происхождение данного символа связывается с церковным преданием о том, что апостол Пётр был распят на кресте вниз головой по своей собственной просьбе, поскольку считал себя недостойным умереть той же смертью, какой умер Иисус Христос. В связи с тем, что Пётр считается основателем Католической Церкви, данный символ изображён на троне папы римского. Например, во время своего визита в Израиль папа Иоанн Павел II сидел на троне с вырезанным в спинке крестом.</p>" },
                new Exhibit() { Code = "cat_lorcross", Name = "Лотарингский крест", Location = "Католицизм", Description = "<p>Лотарингский крест &mdash; геральдическая фигура, представляющая собой крест с двумя поперечинами. Название происходит от Лотарингии &mdash; области на границе Франции и Германии, чьим символом является лотарингский крест.</p>  <p>Лотарингский же крест может изображаться с двумя равными по длине поперечинами; при этом они могут располагаться не только в верхней части фигуры. Этот крест является также крестом Элладской православной церкви. Он использовался, например, в Средние века в Белоруссии и часто встречается там в настоящее время.</p>" },
                new Exhibit() { Code = "cat_ferula", Name = "Папский крест", Location = "Католицизм", Description = "<p>Папский Крест или Ферула &mdash; символ папского служения. Как материальный крест, его несли перед римским папой в процессиях или использовался им как его пастырский посох. Разновидность латинского креста, но с тремя поперечинами.</p>  <p>Поперечины обозначают папское тройное правление как высшего священника, высшего учителя и главного пастыря. Они также символизируют идею о том, что Папа Римский, как представитель Бога на Земле, является соправителем трёх царств: небесного, земного и адского. Число 3 также считается божественным в некоторых культурах. Иногда такой крест называется западным тройным крестом.</p>" },
                new Exhibit() { Code = "cat_jeruscross", Name = "Иерусалимский крест", Location = "Католицизм", Description = "<p>Как утверждают специалисты, такой крест являлся гербом Готфрида Бульонского. Готфрид, граф Бульонский, герцог Нижней Лотарингии - один из лидеров I Крестового похода (1096-1099 г.г.), провозглашенный правителем Иерусалима после взятия города крестоносцами. Готфрид Бульонский отказался от королевского титула, поскольку не счел возможным принять золотой венец в том месте, где Спаситель принял венец терновый, но согласился на титул &laquo;Хранителя и Защитника Гроба Господня&raquo;. В этом качестве новый правитель Вечного города взял в качестве герба пять золотых крестов на фоне цвета серебра.</p>  <p>Видоизмененный иерусалимский крест является символом католического рыцарского Ордена Святого Гроба Господнего Иерусалимского (на латыни Ordo Equestris Sancti Sepulcri Hierosolymitani), имеющего штаб-квартиру в Ватикане и насчитывающего по состоянию на 2010 год более 28 тысяч членов. С 29 августа 2011 года Орденом руководит архиепископ Балтимора (США) Эдвин О`Брайен. К Ордену в разное время принадлежали такие известные люди, как композитор Ференц Лист, канцлер ФРГ Конрад Аденауэр, генерал Петр Николаевич Врангель. Своим основателем Орден считает все того же Готфрида Бульонского.</p>" },
                new Exhibit() { Code = "cat_vatican", Name = "Ватикан", Location = "Католицизм", Description = "<p>Ватикан (лат. Status Civitatis Vatican?, итал. Stato della Citta del Vaticano &mdash; карликовое государство-анклав (самое маленькое официально признанное государство в мире) внутри территории Рима, ассоциированное с Италией. Своё название государство получило от названия холма Mons Vaticanus.</p>  <p>Статус Ватикана в международном праве &mdash; вспомогательная суверенная территория Святого Престола, резиденции высшего духовного руководства римско-католической церкви. Суверенитет Ватикана не является самостоятельным (национальным), а проистекает из суверенитета Святого Престола. Иными словами, его источник &mdash; не население Ватикана, а именно папский престол.</p>  <p>Дипломатические миссии иностранных государств аккредитуются при Святом Престоле, а не при городе-государстве Ватикан. Иностранные посольства и представительства, аккредитованные при Святом Престоле, ввиду малой территории Ватикана, размещены в Риме (включая и посольство Италии, которое, таким образом, находится в её собственной столице).</p>  <p>Святой Престол (не Ватикан) является постоянным наблюдателем при ООН с 1964 года, сотрудничая с организацией с 1957 года. В июле 2004 года права миссии Святого Престола при ООН были расширены. Кроме того, с августа 2008 года Ватикан начал сотрудничество с Интерполом на постоянной основе.</p>" },
                new Exhibit() { Code = "cat_bible", Name = "Библия", Location = "Католицизм", Description = "<p>Библия — каноническое собрание текстов, считающихся священными в иудаизме и христианстве. Православные и католические тексты Библии отличаются от протестантских наличием дополнительных книг в Ветхом Завете, которые рассматриваются как «неканонические» или «девтероканонические» (в католицизме).</p><p>Существуют также отличия в последовательности книг в Библии в различных традициях. Еврейская Библия (Танах) содержит 3 раздела из 24 книг: 5 книг Торы («Учение» или «Закон»), Невиим («Пророки») и Ктувим («Писания»). В Танахе «Пророки» (Невиим) предшествуют Писанию (Ктувим) и включают в себя книги «ранних пророков»: книги Иисуса Навина, Судей, 1 и 2 Самуила (1 и 2 Царств) и 1 и 2 Царей (3 и 4 Царств), которые в христианской традиции рассматриваются как исторические, наравне с книгами Паралипоменон. Кроме этого, в иудейской традиции книга Даниила рассматривается не как пророческая, а как часть Писания.</p><p>Содержание христианских Библий варьируется от протестантского канона (66 книг) до канона Эфиопской православной церкви (81 книга). Первая часть христианской Библии — Ветхий Завет — в основном представляет собой перегруппированную Еврейскую Библию, разделённую на 39 книг. Римско-католическая церковь и Восточное христианство также включают в ветхозаветный канон второканонические книги. Вторая часть — Новый Завет — состоит из 27 книг: 4 канонических Евангелия, Деяния апостолов, 21 послание апостолов и Откровение Иоанна Богослова.</p>" },
                new Exhibit() { Code = "cat_shroud", Name = "Туринская плащаница", Location = "Католицизм", Description = "<p>Туринская плащаница представляет собой очень древнее льняное полотно длинной более чем в четыре метра, на котором имеются золотистые нерукотворные отпечатки обнаженного, жестоко истерзанного Человека. Никто из ее исследователей вот уже сто лет не может сказать каким образом нерукотворные отпечатки появились на ткани, хотя в то же самое время вот уже около двух тыся лет сердца верующих христиан ясно подсказывают Кто и при каких обстоятельствах оставил изображение на этой ткани. Но что же общего между всеми плащаницами как рукотворными так и нерукотворной?</p><p>Принося свои молитвы пред образом на плащанице, мы через этот образ поклоняемся самому Христу Спасителю и Богу нашему, а не той ткани на которой есть Его Изображение, независимо от того сделан этот образ человеческими руками или он нерукотворен. Когда мы молимся перед образом, мы сообщаемся с самим Богом, когда мы целуем ткань с образом - мы молитвенно прикасаемся к самому Христу. Имея глубокую веру в Воскресшего из мертвых, мы посредством образа сообщаемся с Ним - с Первообразом, встречаемся с Ним воочую лицом к лицу, мы сообщаемся с Ним, с Ним - живым и дарующим жизнь, с победителем смерти и ада, с нашим Спасителем!</p>" },
                new Exhibit() { Code = "cat_graali", Name = "Святой Грааль", Location = "Католицизм", Description = "<p>Святой Грааль — в средневековых кельтских и нормандских легендах одно из орудий Страстей — чаша, из которой Иисус Христос вкушал на Тайной вечере и в которую Иосиф Аримафейский собрал кровь из ран распятого на кресте Спасителя.</p><p>Легендарные рыцари Круглого стола проводили свою жизнь в бесплодных поисках Святого Грааля, который (вместе с копьём, пронзившим тело Христа), якобы сохранил и привёз в Британию Иосиф Аримафейский. В европейских средневековых романах Грааль трактуется не как чаша, а как камень или некая драгоценная реликвия.</p><p>Испивший из чаши Грааля получает прощение грехов, вечную жизнь и т. д. В некоторых версиях даже близкое созерцание магического предмета даёт бессмертие, а также различные блага в виде еды, питья и т. п. Слова «Святой Грааль» часто используются в переносном смысле как обозначение какой-либо заветной цели, часто недостижимой или труднодостижимой.</p>" },
                new Exhibit() { Code = "cat_bench", Name = "Скамьи в католической церкви", Location = "Католицизм", Description = "<p>Скамьи обычно делаются из дерева и снабжаются спинкой, а скамеечки нередко обиваются мягкими подушечками. Сбоку на скамьях или на их спинках могут помещаться изображения.</p><p>Традиционно скамьи располагаются в одном общем направлении, то есть одна за другой, лицом к пресвитерию. В некоторых крупных церквах, куда приходит много паломников, скамьи делаются съемными или вообще отсутствуют. Например, в базилике св. Петра вместо них ставятся стулья, или же прихожане вообще стоят. Однако это отнюдь не норма католического обычая, а, скорее, исключение, причина которого - необходимость дать достаточно места для огромного стечения народа, нередко бывающего на Мессах и других церемониях.</p><p>Скамьи способствуют тому, чтобы неф выглядел по-церковному; они - часть католического наследия и известны на Западе по меньшей мере с XIII в., правда, тогда у них не было спинок. К концу XVI столетия большинство строившихся католических храмов имели деревянные скамьи с высокими спинками и скамеечками для коленопреклонения. Но еще до того, как скамьи вошли в употребление, значительную часть Мессы верные проводили на коленях и стоя, а скамьи ставили только для важных особ – королей или сеньоров данной местности. В музеях с коллекциями средневекового искусства можно увидеть эти роскошные скамьи с деревянными резными балдахинами. Красивый мозаичный пол многих старых церквей объясняется именно тем, что скамьи ставили редко и не для всех.</p>" },
                new Exhibit() { Code = "cat_stained", Name = "Витраж", Location = "Католицизм", Description = "<p>Витраж (фр. vitre — оконное стекло, от лат. vitrum — стекло) — произведение декоративного искусства изобразительного или орнаментального характера из цветного стекла, рассчитанное на сквозное освещение и предназначенное для заполнения проёма, чаще всего оконного, в каком-либо архитектурном сооружении.</p><p>С давних пор витраж использовался в храмах.</p><p>В раннехристианском храме окна заполнялись тонкими прозрачными пластинами камня (алебастра, селенита), из которых составляли орнамент.</p><p>В романских храмах (Франция, Германия) появились сюжетные витражи. Многоцветные, большие по размеру витражи из разнообразных по форме стёкол, скреплённых свинцовыми перемычками, являлись особенностью готических соборов. Чаще всего готические витражи изображали религиозные и бытовые сцены. Они размещались в огромных стрельчатых окнах, так называемых «розах». В эпоху Возрождения витраж существовал как живопись на стекле, применялась техника выскабливания по специально покрашенному разноцветному стеклу.</p>" },
                new Exhibit() { Code = "cat_picture_1", Name = "«Взятие Константинополя крестоносцами» (Делакруа, 1840)", Location = "Католицизм", Description = @"<p>На этой картине изображен эпизод Четвертого Крестового похода (1202-04). Тогда крестоносцы, отклонившись от своего маршрута (они шли освобождать Иерусалим), захватили и разграбили Константинополь. Делакруа показал всадников под предводительством Бодуэна Фландрского и молящих о пощаде мирных константинопольцев. Эта работа художника была приобретена государством, пополнив серию исторических полотен Лувра.</p><p>Ее композиция во многом перекликается с ""Резней на Хиосе"". Вновь Делакруа пишет сцену с высокой точки, открывая широкую панораму заднего плана; вновь он театрально располагает группы фигур на переднем плане. Впрочем, ""жестокости"", характерной для ""Резни на Хиосе"", здесь значительно меньше - всадники-крестоносцы выглядят скорее утомленными и даже слегка смущенными, нежели кровожадными.</p>" },
                new Exhibit() { Code = "cat_altar", Name = "Алтарь", Location = "Католицизм", Description = "<p>АЛТАРЬ  (лат. altare, altar, от alta ara — высокий жертвенник) — камень, плита или иное возвышение, предназначенное для совершения жертвоприношений.</p><p>Время появления А. в рел. культах неизвестно. А. для принесения жертв, совершения возлияний и воскурений с целью умилостивления богов или поминовения предков существовал в большинстве культов политеистических религий (политеизм). В дохристианских религиях А. имел вид стола, столба или колонны.</p><p>В первых христ. храмах III–IV в. существовал лишь один А. На Западе несколько А. в храме появились в V–VI вв. Первоначальной причиной этого стала необходимость обеспечить должное почитание мощей различных святых. В VII в. распространение практики вотивных месс и рукоположения монахов стали дополнительными факторами устройства в зап. храмах нескольких А., особенно в силу того, что сохранялось правило ежедневного служения только одной мессы на А. К кон. Средневековья наряду с главным А. в западных храмах устраивалось несколько боковых А., напр. Кафедральный собор Магдебурга и храм Пресв. Девы Марии в Данциге (совр. Гданьск) имели по 48 А. Древний принцип существования только одного А. в храме сохранялся на Западе в значении наличия главного А. в каждой катол. церкви.</p>" }
            };
            exhibits.ForEach(s => context.Exhibits.AddOrUpdate(p => p.Code, s));
            context.SaveChanges();

            var articles = new List<Article> {
                new Article() { Title = "Папство - что это такое?", Text = @"<p>Христианство столь значительно для современного человека не только потому, что это - мировая религия, но и потому, что в течение двух тысяч лет оно играло огромную роль в истории культуры, было органически связано с мировой историей. Эта связь всегда была для христианства существенной и вместе с тем чреватой всевозможными проблемами.</p>  <p>Христианство никогда не хотело быть бесплотной, спиритуалистической религией, которая не играет роли в истории, в формировании человеческого общества, а предлагает лишь спасение, бегство от истории, уход от общества. Так что история христианства, христианской (в частности, католической) церкви и папства тесно переплелась с мировым историческим процессом.</p>  <h5>Прежде чем приступить к историческому исследованию, есть смысл уточнить официальную церковную точку зрения на понятия ""папа"" и ""папство"".</h5>  <p>Термин ""папа"" по-гречески означает ""отец"". В первые века христианства это название применялось для всех епископов, а первоначально - для всех священников, пользовавшихся правом благословения. Имеются свидетельства того, что в VI веке некоторые епископы еще назывались ""папами"". Но начиная с VII века титул ""папа"" давался уже исключительно римскому епископу: это отражало процесс формирования учения католической (западной) церкви о примате (верховенстве) римского епископа.</p>  <p>Согласно этому учению, папа является наследником Святого Петра в римском епископате и в силу этого обладает верховенством в церкви. Власть папы - это высшая и юридически полная власть над всей церковью, независимая от какой бы то ни было человеческой власти и распространяющаяся не только на вопросы веры и нравственности, но и на все управление церковью. Выдающийся венгерский юрист-теолог, кардинал и глава венгерской церкви Юстиниан Шереди так сформулировал положение о законодательной, судебной и наставнической власти папы: ""Римский папа, как зримый глава Храма Христова, милостью Божьей осуществляет высшую, полную, единую и непосредственную юрисдикцию над всеми частями Святой матери-церкви и над всеми ее членами во всех религиозных, моральных, дисциплинарных вопросах и в делах управления церковью...""</p>  <h5>Что же составляет конкретное содержание верховенства (примата) папской власти?</h5>  <p>Папа осуществляет верховную законодательную власть в церкви: папа (и вселенский собор) имеет право издавать законы, обязательные для всей церкви или для ее части, толковать их, изменять или отменять их действие. Законы, издававшиеся соборами и папами по вопросам церковной дисциплины, назывались канонами. Их объединяли в специальные канонические сборники - кодексы канонического права. Уже Халкидонский собор (451) выпустил такой сборник. Средневековое каноническое право было кодифицировано в неоднократно переиздававшемся ""Corpus Juris Canonici"" (""Свод юридических канонов""). Наиболее часто используемым его вариантом было, пожалуй, лейпцигское издание 1876-1884 годов. Свод законов, вступивший в силу 19 мая 1918 года, составила папская кодификационная комиссия, созданная в 1904 году папой Пием Х под руководством кардинала Гаспарри и выпустившая его под названием ""Codex Juris Canonici"" (""Кодекс канонического права""). 27 мая 1917 года папа Бенедикт XV освятил его и опубликовал. В последний раз сборник законов католической церкви был издан между двумя мировыми войнами при активном участии в его подготовке Юстиниана Шереди в 9 томах под названием ""Источник канонического права"" (""Codicis Juris Canonici Fontes"", Рим, 1923-1929 годы, т. 1-7 - ред. П. Гаспарри, т. 8-9 - ред. Шереди).</p>  <p>Папе принадлежит верховная каноническая, апостольская власть в церкви. В вопросах веры и морали папа следит за чистотой вероучения, то есть отвергает псевдоучения, руководит распространением веры (миссионерской деятельностью), созывает вселенские соборы католической церкви, ведет их заседания (лично или посредством уполномоченных им лиц), утверждает их решения, переносит или распускает соборы.</p>  <p>Предмет вероучения - откровения Божьи, хранительницей которых является вся церковь. Церковь сообщает эти откровения на торжественных или обычных богослужениях. Торжественным богослужением называют те редкие службы, на которых церковь оглашает какой-то догмат веры. Это может происходить либо на вселенском соборе, либо путем провозглашения его папой ех cathedra (с кафедры, с амвона). Догматы соборов (а после I Ватиканского собора, с 1870 года, оглашаемые папой ex cathedra) по вопросам веры и морали объявляются как действительные для всей церкви и непогрешимые (infallibitas), то есть они не могут быть ошибочными.</p>  <p>Папа наделен высшей судебной властью в церкви. Любое судебное дело может быть подано ему как первой инстанции. Единственно ему подсудны кардиналы, нунции и епископы, обвиняемые в уголовных преступлениях. Папа разрешает дела в 3-й инстанции по апелляциям в церковных процессах. Запрещается обращаться к светскому суду с обжалованием приговора, вынесенного папой.</p>  <p>Папе принадлежит высшая исполнительная власть в церкви: он учреждает, изменяет и ликвидирует епископства; назначает, утверждает в должности, переводит и смещает епископов; заполняет вакансии, распоряжается на высшем уровне церковным имуществом, осуществляет право посвящения в блаженные и святые.</p>  <p>Внутрицерковные функции папы наиболее четко изложены в книге французского автора Жозефа де Местра ""О папе"" (Париж, 1819). В ней он утверждает, что папа - гарант всеобщности (универсальности) и единства католической церкви. Поскольку папа - залог единства церкви, то и папская власть - суверенна. Через папу действуют в полную силу и части церкви. Единовластие папы необходимо для соблюдения законности и для поддержания порядка внутри церкви. Римская церковь единовластна и не терпит ни демократии, ни аристократии. Без папы нет даже вселенского собора. И следовательно, единовластию папы как форме правления, воплощающей в себе все, сопутствует непогрешимость.</p>  <p>Святой Франциск де Саль считал: ""Церковь и папа - одно целое"". Де Местр пишет, что без папы нет истинного христианства. ""Папа необходим, он - единственный и исключительный отец христианства. Он обладатель обетованного без него исчезает единство, а значит, и церковь"" (Ж. де Местр. О папе. Пешт, 1867. С.360). Природа папской власти - духовная, она исходит от Бога, потому что всякая суверенность божественна. Папский суверенитет был источником власти европейских христианских монархов; поэтому де Местр считает, что папа стоит выше светской власти.</p>  <p>Власть папы, означающая его верховенство над вселенской церковью, дополняется еще и следующими видами рациональной власти, а точнее, они ей сопутствуют: каждый из пап является одновременно епископом города Рима, архиепископом римской Церковной области, кардиналом Италии, патриархом Запада. Эти его функции исторически сложились именно в таком порядке и связаны были с римским епископатом, а потом привели ко вселенскому верховенству. Папа управляет римской Церковной областью как епископ, носящий титул Наместника Христа (Vicarius Urbis).</p>  <p>Вселенское верховенство папы над церковью и его региональные церковные функции в ходе истории - от образования Церковного государства (Patrimonium Sancti Petri) вплоть до 1870 года, до его ликвидации - органически связаны с функцией светского монарха (суверена) Папского государства. Управление Церковным государством в качестве ненаследуемой монархии с течением времени менялось, но природа его всегда оставалась монархической и абсолютистской. Реорганизованное в духе Латеранских соглашений (1929) Папское государство-город Ватикан, - только символ светской власти и независимости пап. (Городом Ватикан папа управляет через своего заместителя по общим вопросам.)</p>", Exhibit = context.Exhibits.SingleOrDefault(p => p.Code == "cat_pope") },
                new Article() { Title = "Титул Папы Римского", Text = @"<p>Официальное, принятое в русском и других языках, титулование Папа не является, на самом деле, официальным титулом Папы Римского.</p>  <p>Наименование Папа в русском языке в титуловании римского первосвященника происходит от греческого &pi;ά&pi;&pi;&alpha;&sigmaf; (pappas) а также латинского (а позднее итальянского) словечка papa, что означает отец. Но что касается церкви, титул papa являлся с начала III и, приблизительно, до VI века всего лишь почетным обращением, использовавшегося для титулования любого епископа в западном христианстве.</p>  <p>В восточном христианстве титулом papa доныне официально пользуется епископ Александрийский &mdash; глава христиан-коптов. Поэтому в русском и ряде др. языков была принята традиция дополнять неофициальный титул западного первосвященника &mdash; Папа словом Римский.</p>  <p>В современном Ватикане титул papa используется и в повседневной терминологии, и в терминологии Ватиканских СМИ, а также в документообороте Святого престола &mdash; Римско-католической церкви (по-итальянски как Papa, по-английски как Pope и.т.д), но при этом титулование Папа не является официальным титулом главы Римско-католической церкви.</p>  <p>Официальный титул Папы Римского звучит как Епископ Рима, викарий Христа, преемник князя апостолов, верховный первосвященник Вселенской церкви, Великий понтифик, Примас Италии, архиепископ и митрополит Римской провинции, суверен государства-города Ватикан, раб рабов Божьих. На латыни это звучит как Episcopus Romanus, Vicarius Christi, Successor principis apostolorum, Caput universalis ecclesiae, Pontifex Maximus, Primatus Italiae, Archiepiscopus ac metropolitanus provinciae ecclesiasticae Romanae, Princeps sui iuris civitatis Vaticanae, Servus Servorum Dei.</p>  <p>В наши дни единственной общеупотребительной частью из официального титула римского первосвященника в прессе и в повседневном языке является титул pontifex. У этого титула интересная история, идущая с дохристианских времен.</p>  <p>Вот, что говорится об этом в книге об истории и памятниках Рима русской исследовательницы Федоровой (1986 г):</p>  <p>&laquo;Официально Папа всегда именовался pontifex maximus; так у римлян назывался глава коллегии жрецов-понтификов, который, с конца VIII в. до н. э. считался главным жрецом государства; папы унаследовали этот языческий титул&raquo;.</p>  <p>Отметим, что буквально лат. термин pontifex означает &laquo;наводящий мосты&raquo;, от слова &laquo;pons&raquo; &mdash; &laquo;мост&raquo; (в смысле &laquo;соединяющий людей и богов&raquo;), а &laquo;maximus&raquo; &mdash; это значит &laquo;верховный&raquo;.</p>  <p>Через весь фасад Собора Святого Петра в Ватикане выбита крупная надпись на латыни с упоминанием основного официального титула Папы Римского pontifex. Надпись гласит: IN HONOREM PRINCIPIS APOST PAVLVS V BVRGHESIVS ROMANVS PONT MAX AN MDCXII PONT VII. (&laquo;Во славу Князя Апостолов, Павел V Боргезе, римлянин, великий понтифик&raquo;, а также указание даты и года понтификата).</p>  <p>Официальный фильм &laquo;Град Ватикан&raquo;, выпущенный Издательством Ватиканских музеев при губернаторстве (правительстве) государства-города Ватикан, так описывает предысторию появления этой надписи на фасаде Соборе Святого Петра (цитата по русской версии):</p>  <p>&laquo;(Нынешняя) Базилика Святого Петра, которая была как бы наложена на первоначальную, именуемую Константиновой (императора Константина и Папы Сильвестра, основавших ее) была завершена в промежутке одного столетия &mdash; с 1506 по 1616 гг., при восемнадцати Папах &mdash; от Юлия II до Павла V, который начертал свое имя на фасаде&raquo;.</p>  <p>Отметим, что вторым основным официальным титулом Папы Римского является титул епископ Рима (от греч. ἐ&pi;ί&sigma;&kappa;&omicron;&pi;&omicron;&sigmaf; &mdash; &laquo;надзирающий&raquo;).</p>  <p>Этот титул очень ценен для Пап, т.к. первым епископ Рима, согласно преданию, являлся Апостол Петр. Однако данный титул обычно не употребляется в повседневной ватиканской лексике и лексике СМИ. О нем обычно лишь вспоминают, когда Папа посещает приходы Римской епархии, епископом которой он продолжает являться.</p>  <p>При этом в повседневных делах епархии Папу представляет Генеральный викарий Рима или кардинал-викарий (итал. Cardinale Vicario). Напомним, что и сам Папа Римский также в своем титуловании имеет титул викарий &mdash; викарий Христа (от лат. vicarius -&laquo;заместитель&raquo;, &laquo;наместник&raquo;).</p>  <p>Вот как описывал происхождение Папского титула епископ Рима и претензию Пап на первенство в христианском мире Митрополит Тадеуш Кондрусевич, &mdash; тогдашний глава католиков европейской России, в своем обращении 19 апреля 2005 года по случаю избрания Папы Бенедикта XVI:</p>  <p>&laquo;Папа &mdash; как Епископ Рима &mdash; является преемником св. Апостола Петра, которому Христос сказал: &laquo;Ты &mdash; Петр, и на сем камне Я создам Церковь Мою, и врата ада не одолеют ее. И дам тебе ключи Царства Небесного; и что свяжешь на земле, то будет связано на небесах; и что разрешишь на земле, то будет разрешено на небесах&raquo; (Мф 16, 18-19). В этих словах сокрыта харизма первенства Петра, особое призвание как его самого, так и его преемников в Церкви Христовой. И хотя Петр трижды отрекся от своего Учителя, тем не менее, Христос после Воскресения подтвердил вверенное служение. Иисус три раз спрашивал Петра, любит ли он Его? Услышав три раза утвердительный ответ, Он поставил Петра Первосвященником Своего стада (ср. Ин 21, 15-17).</p>  <p>Выполняя эту миссию Петр, первый среди Апостолов, прибыл из Иерусалима в Рим и стал его первым епископом. После него пришел черед его преемников.</p>  <p>Папа стал епископом Вечного города, Викарием Христа и Предстоятелем Вселенской Церкви. В нем пребывает служение, вверенное Спасителем Апостолу Петру. Поэтому Понтифик пользуется в Церкви верховной, полной, непосредственной и универсальной ординарной властью&raquo; (орфография послания сохранена).</p>  <p>Кроме вышеназванных, другие официальные титулы Папы Римского почти не употребляются в повседневной жизни. Среди них и такой экзотически звучащий и уже упоминавшийся выше титул как &laquo;преемник князя апостолов&raquo; (от лат. principis apostolorum, что буквально означает &laquo;глава, принц апостолов&raquo;), где под князем понимают Апостола Петра.</p>  <p>Изменения в титуловании Папы Римского можно отслеживать по ватиканскому официальному ежегоднику Annuario Pontificio (&laquo;Понтификальный Ежегодник&raquo;). Не часто, но раз в несколько десятилетий, тот или иной Папа Римский может что-либо видоизменять в своем официальном титуле. Например, с 2006 года титулование Патриарх Запада не употребляется в папском титуловании, согласно решению Папы Бенедикта XVI.</p>  <p>Обращение к Папе звучит как &laquo;Ваше Святейшество&raquo;, а ватиканские СМИ, включая Ватиканское Радио, работающее на многих языках (включая русский), в своих сообщениях обычно запросто называют главу Римско-Католической церкви Святейший отец и Папа.</p>", Exhibit = context.Exhibits.SingleOrDefault(p => p.Code == "cat_pope") },
                new Article() { Title = "Взвышение христианства в средние века", Text = @"<p>После окончательного разрыва с восточной православной церковью в католической церкви было достигнуто догматическое единство; длительное время народные ереси, направленные против церковной иерархии, основывались на различных течениях, отклонившихся от официальной церковной доктрины. Укрепление единства церкви - это не религиозный вопрос, а церковно-административная проблема. Гарантом единства католической церкви стал римский папа. Ссылаясь на высшую власть учения, обусловленного догматами, папа хотел обеспечить также исключительность своего верховенства и в церковно-административной области. Целью его было создание централизованного абсолютистского церковного правления, которому препятствовала государственно-церковная партикулярная раздробленность европейских феодальных государств, укрепившихся к XI веку, их отрыв от центрального (римского) управления.</p>  <p>Стало очевидным, что правители отдельных государств стремятся укрепить свою власть, опираясь на свои усиливающиеся национальные церкви, следовательно, они не были заинтересованы в дальнейшем упрочении центральной церковной власти. Распадение на национальные церкви в то же время таило в себе опасность того, что эти церкви - подобно восточным - станут самостоятельными и в догматических вопросах, что вело к ликвидации универсализма христианства. Таким образом, папы, стремясь к верховенству, не руководствовались лишь желанием добиться этой ограниченной цели, когда требовали для себя права назначения (инвеституры) высшего духовенства, что прежде было прерогативой светской власти, правителей. Высшее духовенство попадало при этом в зависимость от собственных светских правителей и тем самым вынуждено было обслуживать церковно-административные и церковно-политические цели государства. Этому можно было воспрепятствовать лишь путем соблюдения вселенских церковных интересов, воплощенных в папской верховной власти в результате централизованного управления. Тем самым обеспечивалось единство церкви.</p>  <p>Распространение церковно-административной верховной власти папы вовнутрь (внутри церкви) означало, что национальные церкви подчиняются Риму, иерархи церкви зависят от папы, таким образом, реализуется принцип церковного универсализма. Осуществление примата наружу, в отношении к светской власти, означало, что единство церкви можно защитить лишь путем борьбы с партикулярными интересами светских государств; первейшим средством для достижения этой цели была передача Риму права назначать высших чинов церкви. Однако Григорианское папство довело идею до логического конца: попыталось распространить примат папы и на область политики. Примат Святого престола в области догматов уже на протяжении многих веков никто не подвергал сомнению. И в церковной иерархической администрации, хотя и не без сопротивления, верховенство папы было принято. Григорий VII и его преемники путем переосмысления прежнего дуализма в органичном единстве с церковным универсализмом, а также под руководством папы хотели осуществить и политический универсализм. Для реализации этой концепции во главе христианского сообщества должен находиться папа, который занимает и место императора.</p>  <p>Внутренние закономерности феодального общества открывали широкие возможности для осуществления теократии. В период раннего феодализма (IX-XI века) ведущую роль в христианском сообществе играла власть императора; наряду с уже приведенными причинами сопутствующим фактором было то обстоятельство, что отдельные феодальные государства еще не упрочили своего положения, христианство еще не проникло в глубину общества, властвуя лишь на его поверхности. В этой ситуации был реализован примат светской, вооруженной власти.</p>  <p>Положение изменилось в период зрелого феодализма (XII-XIV века). Имперская власть над государствами, в которых укреплялся феодализм, оказалась неосуществимой, политический универсализм нельзя было реализовать с помощью государственно-властных средств, опираясь на одну империю (и лишь в рамках Германо-римской империи). Произошли изменения и внутренней структуры общества, развитие феодальных отношений привело к укреплению центральной королевской власти. В этот период все сферы общества оказываются пронизаны христианством, религия превращается в органическую часть общества. Универсальная имперская власть оказалась слабее партикулярных сил, в то же время церковь, а внутри ее религиозный и административно-церковный универсализм папства укрепился и почти достиг абсолюта. Начиная с середины средних веков папство в своем развитии превратилось в единственную универсальную власть, и это позволило предпринять попытку добиться также политического универсализма. Политическая верховная власть, реализованная папой, была достигнута не с помощью государственно-властных средств (с помощью оружия), а в идеологической и политической сфере, но при одновременной опоре на крепнущее суверенное Папское государство.</p>", Exhibit = context.Exhibits.SingleOrDefault(p => p.Code == "cat_pope") },
                new Article() { Title = "Папство на заре нового времени", Text = @"<p>Переход от средневековья к Новому времени, охватывающий два столетия, характеризовался в истории папства большими противоречиями и изменениями. Монархическое руководство католической церковью постепенно отрывается от средневековой политической панорамы мира и, обновившись, к концу XVI века с новой энергией выступает против секуляризирующейся Европы Нового времени.</p>  <p>Переход от феодализма к буржуазному обществу проявился в религиозном отношении как в политической, так и в идеологической сфере (в тех рамках, в которых функционировало папство). Примерно десять пап второй половины XV века - начала XVI века делают папство великой духовной силой гуманизма и Ренессанса. Тем самым и церковь, и ее руководство созревают для неизбежной реформы. Вторжение идей гуманизма в курию и становление самого папского двора как культурного и политического центра Ренессанса - прекрасный пример разрыва со средневековым аскетизмом, отказа от средневекового мировосприятия.</p>  <p>Однако для внутреннего с точки зрения содержания обновления церкви и папства были необходимы внешние импульсы, движущая сила буржуазного развития. Это было привнесено немецкой Реформацией, а вслед за ней и распространившимся на все христианство обновлением веры. История папства вступила в период глубочайшего кризиса, так как оно, наверное, на протяжении полувека оказалось почти бессильным перед лицом Реформации. Когда разрыв казался уже роковым и политико-великодержавные средства оказались однозначно негодными для приостановления обновления веры, внутри церкви также начался - сначала при сопротивлении папы, а затем при его поддержке - процесс обновления, католической реформы.</p>  <p>Из оборонительной позиции по отношению к Реформации папство вывел Тридентский вселенский собор. Решения Тридентского собора на полтысячи лет закрепили догматы католической церкви, а на обновление веры ответили новациями, реформами. Укрепленное и обновленное здание церкви не только выдержало бури Реформации, но вскоре оказалось способным отвоевать часть утерянных позиций. После прошедшего в середине XVI века вселенского собора средневековая папская монархия не просто была восстановлена, а возник последовательный по своим идеям, современный по своим средствам, использующий все виды духовного оружия папский абсолютизм. Этот современный папский абсолютизм был зеркальным отражением расцветавшего в рамках национальных государств светского абсолютизма.</p>  <p>В результате активной папской деятельности, направленной на осуществление католической реформы, Рим стал в конце XVI - первой половине XVII века центром агрессивной контрреформации, целью которой было отвоевание утраченных позиций. На службу этой цели были поставлены в одинаковой мере политическая и военная сила католических монархий, гигантски выросшая машина инквизиции, запрещающие списки и папские конгрегации. Однако в эпоху религиозных войн выяснилось, что протекающие на заре Нового времени, даже начавшиеся под религиозными лозунгами сражения за власть проходят не по рецептам папства, а как таковые имеют свою самоцель: это бои за национальные или династические интересы. Тридцатилетняя война была еще религиозной войной, но, когда она кончилась, противостояли друг другу уже не католики и протестанты, а католики и католики. В соответствии с этим папа не раз поддерживал католического французского короля, выступавшего в союзе с протестантами, против католического императора. При заключении Вестфальского мира интересы папства уже не были учтены, и его значение в секуляризирующейся политической сфере и в сфере власти было сведено до минимума. Если попытаться периодизировать историю соответственно основным проблемам исторического развития, то увертюрой к Новому времени, к буржуазной эпохе была Английская буржуазная революция (1640), а для папства ею однозначно был Вестфальский мир 1648 года.</p>", Exhibit = context.Exhibits.SingleOrDefault(p => p.Code == "cat_pope") },
                new Article() { Title = "Папство наших дней", Text = @"<p>Ныне можно уже считать несомненным фактом, что в 1958 году с Иоанна XXIII и созванного им собора реформ началась новая эпоха, новое направление в истории папства и римско-католической церкви. Аджорнаменто (aggiornamento - обновление, модернизация, приведение в соответствие с духом времени) вызвало самые значительные изменения в церкви со времени Тридентского собора: отграничение от светской власти и требование возвращения к первозданной миссии древней церкви; литургические последствия поворота к миру и народу, богослужение на национальном языке; претворение в жизнь коллегиальности были, можно сказать, революционными по своему значению шагами, направленными на модернизацию церкви и папства.</p>  <p>Аджорнаменто и II Ватиканский собор разрушили (хотя и с большим опозданием) союз церкви и папства лишь с одной частью мира. Католицизм больше уже не мог отгородиться от ответов на вопросы, поставленные на повестку дня самой жизнью. Новое ""разрешение"" сделало приемлемым для католиков и другие общественно-политические системы, в частности и социализм. Поиск возможности сосуществования с социалистическими странами и диалог с неверующими, атеистами и марксистами развертывались под знаком открытия окна в мир, обновления, подключения к истории. Папство стало ангажированным сторонником общественного прогресса и мира и в соответствии с этим руководило вселенской церковью. На этом пути Ватикан не только восстановил свой религиозно-нравственный авторитет, но и смог вернуться на форум международной политики.</p>  <p>Аджорнаменто и собор потрясли консервативно-реакционное церковное здание, с тем чтобы укрепить основы и построить на них современное, соответствующее эпохе сооружение. Во время длившегося полтора десятилетия понтификата Павла VI устранение внутренних неурядиц, вытекающих из новой адаптации, сделало возможным и необходимым продуманное продвижение церкви вперед. Папа Павел стремился к осуществлению церковных реформ в духе собора, чтобы его преемники активизировались затем в мире, стоя во главе обновленной церкви.</p>  <p>Подходя к нашим дням, можно считать открытым вопрос, куда и насколько далеко пойдут католическая церковь и римский папа. История папства доказывает, что в Риме неизменность и изменяемость осуществляются в диалектическом единстве. Несомненно и то, что церковь на протяжении своей 2000-летней истории - раньше или позже, более или менее гладко, ценой больших потрясений - всегда присоединялась к основному курсу истории. Вероятно, так будет и впредь.</p>", Exhibit = context.Exhibits.SingleOrDefault(p => p.Code == "cat_pope") },
                new Article() { Title = "Распятие", Text = @"<p>Распятие &mdash; крест, изображение Распятия Христова, как правило, скульптурное или рельефное. Являлся важной частью декоративного убранства храмов. Особенной натуралистичностью в изображении страданий и ран Христа отличаются испанские распятия эпохи барокко.</p><p>В католическом Распятии изображение Христа имеет натуралистические черты. Католики изображают Христа мертвым, иногда с потоками крови на лице, из ран на руках, ногах и ребрах (стигматы). В нем проявляется все человеческое страдание, муки, которые пришлось испытать Иисусу. Его руки провисают под тяжестью тела. Образ Христа на католическом кресте правдоподобен, но это изображение мертвого человека, в то время как нет никакого намека на торжество победы над смертью. Распятие в православии как раз таки символизирует это торжество. Кроме того, ноги Спасителя прибиты одним гвоздем.</p>", Exhibit = context.Exhibits.SingleOrDefault(p => p.Code == "cat_latcross") },
                new Article() { Title = "Сатанинское и антихристианское представление креста Св. Петра", Text = @"<p>Христианский крест в перевёрнутом виде может пониматься как антихристианский символ. В силу этого перевёрнутый крест получил широкое распространение в современной массовой культуре как символ сатанизма. В популярной культуре, включая такие фильмы как &laquo;Шесть демонов Эмили Роуз&raquo;, &laquo;Омен&raquo;, &laquo;Сверхъестественное&raquo;, перевёрнутый крест часто показывается как символ Сатаны. Наряду с перевёрнутой пентаграммой, перевёрнутый крест иногда используется музыкантами, играющими блэк-метал.</p>  <p>В любом случае в римском католицизме крест Св. Петра не рассматривается как сатанинский символ. Однако перевёрнутое распятие несёт смысл крайнего непочтения к христианской религии и может использоваться для представления сил Сатаны. Различия между крестом Св. Петра и перевёрнутым распятием иногда затеняются, что приводит к путанице в вопросах приемлемости каждого символа. Подобная неразбериха возникла после вышеупомянутого визита папы в Израиль. Фотография папы, сидящего на троне с крестом Св. Петра, курсировала по Интернету и использовалась в попытках ""доказать"", что католическая церковь связана с сатанизмом.</p>", Exhibit = context.Exhibits.SingleOrDefault(p => p.Code == "cat_petercross") },
                new Article() { Title = "Происхождение лотарингского креста", Text = @"<p>Крест получил своё название от Лотарингии &mdash; провинции на границе Франции и Германии. В Средние века эта провинция была независимым княжеством. В крестовом походе в 1099 году Иерусалим был взят, а победа была посвящена князю Лотарингии.</p>  <p>Существует ряд версий появления креста с двумя перекладинами в Лотарингии. Одна из них связывает его с именем лотарингского короля Звентибольда, правившего в 895&mdash;900 годах. Он был незаконнорожденным сыном германского императора Арнульфа Каринтийского и крестником короля Великоморавской державы Святополка I. Эта версия не получила широкого распространения &mdash; в первую очередь потому что, хотя связи Лотарингии и Великоморавской державы несомненны, свидетельства использования такой формы креста в Лотарингии того времени отсутствуют.</p>  <p>Наиболее распространённая версия выводит в качестве источника появления креста в геральдической практике Людовика I, герцога Анжуйского (1356&mdash;1360). При этом крест выступает в качестве символа реликвии &mdash; &laquo;Истинного креста&raquo;, попавшей во Францию намного раньше. Традиция связывает эту реликвию с именем латинского патриарха Константинополя Гервазия (умершего в 1219 году), от которого она попала к Фоме, епископу Иерапетры (на острове Крит), им была продана в 1241 году Жану Аллюи, а Жан Аллюи в 1244 году продал её аббатству Буасье в Анжу. Здесь крест становится одной из реликвий анжуйской династии, и начиная с Людовика I используется в качестве одного из её династических символов &mdash; в частности, на знамёнах, монетах и т. д.</p>  <p>Рене Анжуйский сделал шестиконечный крест личной эмблемой, а его внук, Рене II Лотарингский, использовал шестиконечный крест в битве при Нанси (1477 год) против герцога Бургундии Карла Смелого в качестве отличительного знака, в противовес бургундскому андреевскому кресту. Именно с этого времени крест получает наименование &laquo;лотарингского&raquo; и, в частности, попадает на некоторые городские гербы. Стоит также отметить, что герцоги Лотарингские использовали шестиконечный крест в качестве вспомогательного элемента личных гербов.</p>", Exhibit = context.Exhibits.SingleOrDefault(p => p.Code == "cat_lorcross") },
                new Article() { Title = "Использование Иерусалимского креста", Text = @"<p>Иерусалимский крест часто изображается на покрывалах на алтаре.</p>  <p>Один из вариантов Иерусалимского креста изображён на флаге Грузии.</p>  <p>Существует несколько модификаций &nbsp;Иерусалимского креста &mdash; крест красного цвета является символом ордена Гроба Господня, а также других &nbsp;военно-монашеских и духовных орденов, в частности Ордена Храма Соломонова (тамплиеров).</p>  <p>Мозаика в виде Иерусалимского креста изображена на надгробии Егора Летова, лидера группы Гражданская Оборона. Егор также носил &laquo;Вселенский&raquo; крест, как он его сам называл, как нательный крест. &nbsp;О его значении Летов говорил так: &laquo;&hellip; Крест этот &mdash; так называемый вселенский, крест первых христиан катакомбного периода, крест, объединяющий все христианские церкви&raquo;.</p>", Exhibit = context.Exhibits.SingleOrDefault(p => p.Code == "cat_jeruscross") },
                new Article() { Title = "Собор Святого Петра", Text = @"<h3>История</h3>  <p>Когда-то на том месте, где теперь стоит собор св. Петра, располагались сады цирка Нерона (от него, кстати, остался обелиск из Гелиополя, который до сего дня стоит на площади Св. Петра). Первая базилика была построена в 326 году, в правление первого христианского императора Константина. Алтарь собора был размещён над могилой, со второго века считаемой захоронением св. Петра, принявшего в 66 году мученическую кончину в цирке Нерона. Во втором соборе в 800 году папа Лев III короновал Карла императором Запада. В XV веке базилика, существовавшая уже одиннадцать столетий, грозила обрушиться, и при Николае V её начали расширять и перестраивать. Кардинально решил этот вопрос Юлий II, приказавший построить на месте древней базилики огромный новый собор, который должен был затмить как языческие храмы, так и существовавшие христианские церкви, способствуя тем самым укреплению папского государства и распространению влияния католицизма.</p>  <p>Почти все крупные архитекторы Италии по очереди участвовали в проектировании и строительстве собора св. Петра. В 1506 г. был утверждён проект архитектора Донато Браманте, в соответствии с которым начали возводить центрическое сооружение в форме греческого креста (с равными сторонами). После смерти Браманте строительство возглавил Рафаэль, вернувшийся к традиционной форме латинского креста (с удлинённой четвёртой стороной), затем Бальдассаре Перуцци, остановившийся на центрическом сооружении, и Антонио да Сангалло, избравший базиликальную форму. Наконец, в 1546 г. руководство работами было поручено Микеланджело. Он вернулся к идее центральнокупольного сооружения, но его проект предусматривал создание многоколонного входного портика с восточной стороны (в древнейших базиликах Рима, как и в античных храмах, вход находился с восточной, а не с западной стороны). Все несущие конструкции Микеланджело сделал более массивными и выделил главное пространство. Он возвёл барабан центрального купола, но сам купол достраивал уже после его смерти (1564 г.) Джакомо делла Порта, придавший ему более вытянутые очертания. Из четырёх малых куполов, предусмотренных проектом Микеланджело, архитектор Виньола возвёл только два. В наибольшей степени архитектурные формы именно в том виде, как они были задуманы Микеланджело, сохранились с алтарной, западной стороны.</p>  <p>В начале XVII в. по указанию Павла V архитектор Карло Мадерно удлинил восточную ветвь креста &mdash; пристроил к центрическому зданию трёхнефную базиликальную часть, вернувшись таким образом к форме латинского креста, и построил фасад. В результате купол оказался скрыт фасадом, утратил своё доминирующее значение и воспринимается только издали, с виа делла Кончильяционе.</p>  <p>Требовалась площадь, которая вмещала бы большое количество верующих, стекающихся к собору, чтобы получить папское благословение или принять участие в религиозных празднествах. Эту задачу выполнил Джованни Лоренцо Бернини, создавший в 1656&mdash;1667 гг. площадь перед собором &mdash; одно из самых выдающихся произведений мировой градостроительной практики.</p>  <h4>Фасад</h4>  <p>Высота фасада, построенного архитектором Мадерна, 45 м, ширина &mdash; 115 м. Аттик фасада венчают громадные, высотой 5,65 м, статуи Христа, Иоанна Крестителя и одиннадцати апостолов (кроме апостола Петра). Из портика пять порталов ведут в собор. Створки дверей центрального портала выполнены в середине XV в. и происходят из старой базилики. Напротив этого портала, над входом в портик, находится прославленная мозаика Джотто конца XIII в. &laquo;Навичелла&raquo;. Рельефы крайнего левого портала &mdash; &laquo;Врат смерти&raquo; &mdash; созданы в 1949&mdash;1964 гг. крупным скульптором Джакомо Манцу. Очень выразителен образ папы Иоанна XXIII.</p>  <h3>Купол</h3>  <p>Купол собора возвышается на высоту 136,57 метров от пола базилики до верхушки венчающего креста. Это самый высокий купол в мире. Его внутренний диаметр составляет 41,47 метров, что чуть меньше чем у куполов-предшественников: диаметр купола Пантеона (Древний Рим) составляет 43,3 метра, диаметр Санта-Мария-дель-Фьоре времён раннего Ренессанса &mdash; 44 метра, однако превосходит купол собора Святой Софии в Константинополе, возведённого в 537 году. Именно Пантенон и флорентийский собор служили примерами для архитекторов собора Святого Петра в плане решений по возведению столь грандиозной конструкции.</p>  <h3>Интересные факты</h3>  <ul>  <li>В 2007 году в архивах Ватикана была найдена последняя работа Микеланджело Буонарроти, выполненная незадолго до его смерти. Это зарисовка, на которой красным мелом изображена деталь одной из радиальных колонн, составляющих барабан купола собора Святого Петра в Риме.</li>  <li>Алтарь собора св. Петра обращён на запад, а не на восток, как большинство христианских храмов.</li>  <li>Крупнейшая в мире христианская церковь &mdash; базилика Нотр-Дам-де-ла-Пэ в Ямусукро &mdash; возведена по образцу Собора Святого Петра.</li>  <li>При Соборе Святого Петра работала собственная мозаичная мастерская. В 1803 году папой Пием VII художник Винченцо Камуччини был назначен её директором.</li>  </ul>", Exhibit = context.Exhibits.SingleOrDefault(p => p.Code == "cat_vatican") },
                new Article() { Title = "Взятие Константинополя", Text = @"<p>Взятие Константинополя (13 апреля 1204 года) войсками крестоносцев было одним из эпохальных событий средневековой истории и имело далеко идущие последствия для всей Европы. Взятию предшествовали две довольно напряжённые осады 1203 и 1204 годов, в ходе которых свои усилия объединили венецианский флот и западноевропейская (преимущественно французская) пехота. После захвата города начались массовые грабежи и убийства греко-православного населения, что было своего рода местью за резню латинян греками в 1182 году. 9 мая новым императором был провозглашён Балдуин Фландрский, что положило начало формированию целой плеяды «латинских» государств на захваченных крестоносцами территориях, хотя греческая знать на периферии империи не покорилась и продолжала борьбу.</p><p>В общем и целом, падение Константинополя объяснялось нарастающим отставанием социально-экономического развития империи по сравнению с более компактными и лучше организованными западноевропейскими государствами, в которых наметилась тенденция к практическому применению последних достижений технического прогресса в быту, армии и флоте, а также к росту торговли и товарооборота, которым сопутствовали растущий потребительский спрос и интенсивный денежный оборот в городах, где появились зачатки финансово-буржуазной инфраструктуры. Византийская знать по-прежнему предпочитала вкладывать свои сбережения в малодоходную, но высокостатусную недвижимость (латифундии в Малой Азии), которую становилось всё труднее содержать и охранять, особенно в условиях тюркских нашествий. В самом Константинополе к концу ХII века появился греческий торговый класс, но он был скорее следствием имитации итальянских купеческих традиций и, в какой-то мере, даже заинтересован в ещё более тесном сотрудничестве с итальянскими талассократиями, с помощью которых рассчитывал улучшить своё благосостояние. В условиях постепенно нарастающего западного присутствия эта группа стала играть роль пятой колонны.</p><p>Крестоносцы уже давно наблюдали за слабеющим городом. За время прошедшее с начала крестовых походов, латиняне успели хорошо ознакомиться с географией Балкан и Малой Азии.</p><p>После взятия города началось массовое мародёрство. Около 2 тыс. человек было убито в первые дни после захвата. В городе бушевали пожары. В огне были уничтожены многие памятники культуры и литературы, хранившиеся здесь с античных времён. Особенно сильно от огня пострадала знаменитая Константинопольская библиотека.</p><p>Осенью 1204 г. комитет из 24 представителей оккупационных сил подписал Договор о разделе Византийской империи (Partitio terrarum imperii Romaniae), положивший начало длительному периоду франкократии.</p><p>Греческое население массово покидало столицу. К концу крестоносской власти в разграбленном городе оставалось не более 50 тыс. жителей.</p>", Exhibit = context.Exhibits.SingleOrDefault(p => p.Code == "cat_picture_1") }
            };
            articles.ForEach(s => context.Articles.AddOrUpdate(p => p.Title, s));
